pragma solidity 0.8.10;

import "../utils/AccessControl.sol";
import "../utils/Pausable.sol";
import "../utils/Initializable.sol";
import "../utils/ReentrancyGuard.sol";
import {Errors} from "../helpers/Errors.sol";

contract BasePausable is
    AccessControl,
    Pausable,
    Initializable,
    ReentrancyGuard
{
    /// @notice keccak256("OWNER_ROLE")
    bytes32 public constant OWNER_ROLE =
        0xb19546dff01e856fb3f010c267a7b1c60363cf8a4664e21cc89c26224620214e;

    modifier onlyOwner() {
        require(isOwner(_msgSender()), Errors.CALLER_NOT_OWNER);
        _;
    }

    function __BasePausable__init(address owner) public onlyInitializing {
        require(owner != address(0), Errors.ZERO_ADDRESS_NOT_VALID);

        _setupRole(OWNER_ROLE, owner);
        _setRoleAdmin(OWNER_ROLE, OWNER_ROLE);
    }

    function isOwner(address sender) public view returns (bool) {
        return hasRole(OWNER_ROLE, sender);
    }
}
","keccak256":"0xd60b49f9757e45b97ebbd1835ddec35533cece06240624aeafbe71f8e632940a","license":"MIT"},"contracts/core/Vesting.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../interfaces/IVesting.sol";
import "./VestingStorage.sol";
import "./BasePausable.sol";
import "../libraries/SafeCast.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../logics/VestingLogic.sol";

contract Vesting is IVesting, VestingStorage, BasePausable {
    using SafeERC20 for IERC20withDec;

    // ============================== EVENT ==============================

    event SetClaimableStatus(bool _status);

    event UpdateTGEDate(uint64 _newTGEDate);

    event SetIDOTokenAddress(address _IDOToken);

    event Funded(bool _status);

    event WithdrawRedundantIDOToken(
        address _beneficiary,
        uint _redundantAmount
    );

    event ClaimIDOToken(
        address sender,
        address _beneficiary,
        uint claimableAmount
    );

    // ============================== MODIFIER ==============================
    modifier afterTGEDate() {
        require(
            block.timestamp >= TGEDate,
            Errors.NOT_ALLOWED_TO_TRANSFER_BEFORE_TGE_DATE
        );
        _;
    }

    modifier onlySatisfyClaimCondition() {
        require(
            isFunded() && isClaimable(),
            Errors.NOT_ALLOWED_TO_CLAIM_IDO_TOKEN
        );
        _;
    }

    // ============================== EXTERNAL FUNCTION ==============================

    function initialize(
        address owner,
        address _IDOToken,
        uint _TGEDate,
        uint _TGEPercentage,
        uint _vestingCliff,
        uint _vestingFrequency,
        uint _numberOfVestingRelease
    ) external override initializer {
        VestingLogic._verifyVestingInfo(_TGEPercentage);
        IDOToken = IERC20withDec(_IDOToken);
        TGEDate = SafeCast.toUint64(_TGEDate);
        TGEPercentage = SafeCast.toUint16(_TGEPercentage);
        vestingCliff = SafeCast.toUint64(_vestingCliff);
        vestingFrequency = SafeCast.toUint64(_vestingFrequency);
        numberOfVestingRelease = _numberOfVestingRelease;
        claimable = true;

        _setupRole(OWNER_ROLE, owner);
        _setRoleAdmin(OWNER_ROLE, OWNER_ROLE);
    }

    function createVestingSchedule(
        address _user,
        uint _totalAmount
    ) external onlyOwner {
        require(_totalAmount > 0, Errors.ZERO_AMOUNT_NOT_VALID);

        vestingAmountInfo[_user].totalAmount += _totalAmount;
    }

    function setIDOToken(IERC20withDec _IDOToken) external onlyOwner {
        IDOToken = _IDOToken;
        emit SetIDOTokenAddress(address(_IDOToken));
    }

    function setFundedStatus(bool _status) external onlyOwner {
        funded = _status;
        emit Funded(_status);
    }

    function setClaimableStatus(bool _status) external onlyOwner {
        claimable = _status;
        emit SetClaimableStatus(_status);
    }

    function updateTGEDate(uint64 _TGEDate) external onlyOwner {
        TGEDate = _TGEDate;
        emit UpdateTGEDate(_TGEDate);
    }

    function claimIDOToken(
        address _beneficiary
    ) external onlySatisfyClaimCondition nonReentrant afterTGEDate {
        VestingAmountInfo storage userInfo = vestingAmountInfo[_msgSender()];
        require(
            userInfo.claimedAmount < userInfo.totalAmount,
            Errors.ALREADY_CLAIM_TOTAL_AMOUNT
        );
        uint claimableAmount = getClaimableAmount(_msgSender());
        require(claimableAmount > 0, Errors.INVALID_CLAIMABLE_AMOUNT);

        claimableAmount = claimableAmount <= IDOToken.balanceOf(address(this))
            ? claimableAmount
            : IDOToken.balanceOf(address(this));
        userInfo.claimedAmount += claimableAmount;

        IERC20withDec(IDOToken).safeTransfer(_beneficiary, claimableAmount);

        emit ClaimIDOToken(_msgSender(), _beneficiary, claimableAmount);
    }

    function withdrawRedundantIDOToken(
        address _beneficiary,
        uint _redundantAmount
    ) external onlyOwner nonReentrant {
        require(_redundantAmount > 0, Errors.ZERO_AMOUNT_NOT_VALID);
        IDOToken.safeTransfer(_beneficiary, _redundantAmount);

        emit WithdrawRedundantIDOToken(_beneficiary, _redundantAmount);
    }

    function getIDOToken() external view returns (IERC20withDec) {
        return IDOToken;
    }

    function getVestingInfo()
        external
        view
        returns (uint64, uint16, uint64, uint64, uint)
    {
        return (
            TGEDate,
            TGEPercentage,
            vestingCliff,
            vestingFrequency,
            numberOfVestingRelease
        );
    }

    // ============================== PUBLIC FUNCTION ==============================

    function isClaimable() public view returns (bool) {
        return claimable;
    }

    function isFunded() public view returns (bool) {
        return funded;
    }

    function getClaimableAmount(address user) public view returns (uint) {
        VestingAmountInfo memory userInfo = vestingAmountInfo[user];
        return
            VestingLogic.calculateClaimableAmount(
                userInfo.totalAmount,
                userInfo.claimedAmount,
                TGEPercentage,
                TGEDate,
                vestingCliff,
                vestingFrequency,
                numberOfVestingRelease
            );
    }
}
","keccak256":"0xd3705a5c3e9bb63416a265b29c1966ca4ad12ee6881c1693dfe09cee7e6178d5","license":"MIT"},"contracts/core/VestingStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../interfaces/IERC20withDec.sol";

contract VestingStorage {
    struct VestingAmountInfo {
        uint totalAmount; // total amount will be claimed by investor
        uint claimedAmount; // claimed amount
    }

    /// @notice Percentage denominator
    uint16 public constant PERCENTAGE_DENOMINATOR = 10000;

    /// @notice Address of IDO token
    IERC20withDec public IDOToken;

    /// @notice Time for user to redeem IDO token
    uint64 public TGEDate;

    /// @notice Percentage of IDO token amount of user, which can be redeemed after TGEDate
    uint16 public TGEPercentage;

    /// @notice Vesting cliff
    uint64 public vestingCliff;

    /// @notice Vesting frequency
    uint64 public vestingFrequency;

    /// @notice Number of vesting release
    uint public numberOfVestingRelease;

    /// @notice vesting info of each user
    mapping(address => VestingAmountInfo) public vestingAmountInfo;

    /// @notice True if collaborator fund enough IDO token
    bool public funded;

    /// @notice True if admin allow user to claim
    bool public claimable = true;
}
","keccak256":"0xe169afe0bc7d523a54e183a3120df378e8fc5be4487e5ae51ba6fd2fd705333a","license":"MIT"},"contracts/helpers/Errors.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

library Errors {
    string public constant CALLER_NOT_ADMIN = "1"; // 'The caller of the function is not an admin'
    string public constant CALLER_NOT_OWNER = "2"; // 'The caller of the funtion is not an owner'
    string public constant ZERO_AMOUNT_NOT_VALID = "3"; // 'Amount must be greater than 0'
    string public constant ZERO_ADDRESS_NOT_VALID = "4"; // 'Zero address not valid'
    string public constant INVALID_TOKEN_FEE_PERCENTAGE = "5"; // 'Token fee must not be greater than 100%'
    string public constant INVALID_TGE_PERCENTAGE = "6"; // 'TGE must be smaller than or equal 100%'
    string public constant INVALID_GALAXY_POOL_PROPORTION = "7"; // 'Galaxy pool proportion must be greater than 0% and smaller than 100%'
    string public constant INVALID_EARLY_ACCESS_PROPORTION = "8"; // 'Early access proportion must be smaller than 100%'
    string public constant INVALID_TIME = "9"; // 'Open time must be smaller than close time, close time for whale must not be greater than open time for community and not be greater than TGE date'
    string public constant INVALID_SIGNER = "10"; // 'Signer does not have the right to approve'
    string public constant INVALID_CLAIMABLE_AMOUNT = "11"; // 'Claimable amount must be greater than 0'
    string public constant NOT_IN_WHALE_LIST = "12"; // 'Investor must be in whale list'
    string public constant NOT_IN_INVESTOR_LIST = "13"; // 'Investor must be in the list'
    string public constant NOT_ENOUGH_ALLOWANCE = "14"; // 'Investor need to approve or permit pool for their token before invest'
    string public constant NOT_FUNDED = "15"; // 'Collaborator need to fund enough IDO token'
    string public constant ALREADY_CLAIM_TOTAL_AMOUNT = "16"; // 'User already claim all of their token'
    string public constant TIME_OUT_TO_BUY_IDO_TOKEN = "17"; // 'Time out for investor to buy IDO token'
    string public constant EXCEED_MAX_PURCHASE_AMOUNT_FOR_USER = "18"; // 'Investor can not buy exceed allocated amount'
    string public constant EXCEED_TOTAL_RAISE_AMOUNT = "19"; // 'Investor can not buy exceed total IDO token raise amount'
    string public constant EXCEED_MAX_PURCHASE_AMOUNT_FOR_KYC_USER = "20"; // 'Investor who already kyced can not buy exceed allocated amount for KYCed user'
    string public constant EXCEED_MAX_PURCHASE_AMOUNT_FOR_NOT_KYC_USER = "21"; // 'Investor who not kyc can not buy exceed allocated amount for not KYC user'
    string public constant EXCEED_MAX_PURCHASE_AMOUNT_FOR_EARLY_ACCESS = "22"; // 'Investor can not buy exceed allocated amount for early access'
    string public constant NOT_ALLOWED_TO_CLAIM_IDO_TOKEN = "23"; // 'Need admin's allowance to claim IDO token'
    string public constant NOT_ALLOWED_TO_CLAIM_TOKEN_FEE = "24"; // 'Admin can claim only one single time'
    string public constant NOT_ALLOWED_TO_DO_AFTER_TGE_DATE = "25"; // 'User must execute before TGE Date'
    string public constant NOT_ALLOWED_TO_CLAIM_PARTICIPATION_FEE = "26"; // 'Admin can claim only one single time'
    string public constant NOT_ALLOWED_TO_WITHDRAW_PURCHASED_AMOUNT = "27"; // 'Investor can withdraw their purchased amount if project failed and only one single time'
    string public constant NOT_ALLOWED_TO_FUND_AFTER_TGE_DATE = "28"; // 'Collaborator must fund IDO token before TGE date'
    string public constant NOT_ALLOWED_TO_ALLOW_INVESTOR_TO_CLAIM = "29"; // 'Admin can set claimable status to true if project success'
    string public constant NOT_ALLOWED_TO_CLAIM_PURCHASE_TOKEN = "30"; // 'Collaborator can only claim profit (which exclude token fee) when project successes'
    string public constant NOT_ALLOWED_TO_TRANSFER_BEFORE_TGE_DATE = "31"; // 'User can only claim or withdraw after TGE date'
}
