contracts/core/BasePausable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../utils/AccessControl.sol";
import "../utils/Pausable.sol";
import "../utils/Initializable.sol";
import "../utils/ReentrancyGuard.sol";
import {Errors} from "../helpers/Errors.sol";

contract BasePausable is
    AccessControl,
    Pausable,
    Initializable,
    ReentrancyGuard
{
    /// @notice keccak256("OWNER_ROLE")
    bytes32 public constant OWNER_ROLE =
        0xb19546dff01e856fb3f010c267a7b1c60363cf8a4664e21cc89c26224620214e;

    modifier onlyOwner() {
        require(isOwner(_msgSender()), Errors.CALLER_NOT_OWNER);
        _;
    }

    function __BasePausable__init(address owner) public onlyInitializing {
        require(owner != address(0), Errors.ZERO_ADDRESS_NOT_VALID);

        _setupRole(OWNER_ROLE, owner);
        _setRoleAdmin(OWNER_ROLE, OWNER_ROLE);
    }

    function isOwner(address sender) public view returns (bool) {
        return hasRole(OWNER_ROLE, sender);
    }
}

contracts/core/IgnitionFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "./BasePausable.sol";
import "../libraries/Clones.sol";
import "../interfaces/IPool.sol";
import "../interfaces/IIgnitionFactory.sol";
import "../interfaces/IVesting.sol";
import "../interfaces/IIgnitionFactory.sol";

contract IgnitionFactory is BasePausable {
    /// @dev Address of pool implementation
    address public poolImplementationAddress;

    /// @dev Address of vesting implementation
    address public vestingImplementationAddress;

    // ============================== EVENT ==============================

    event UpdatePoolImplementation(
        address indexed oldPoolImplementation,
        address indexed newPoolImplementation
    );

    event UpdateVestingImplementation(
        address indexed oldVestingImplementation,
        address indexed newVestingImplementation
    );

    event PoolCreated(bytes32 poolInfoHash, address pool);

    event VestingCreated(address sender, address vesting);

    // ============================== EXTERNAL FUNCTION ==============================

    /**
     * @notice Initialize pool factory with address of pool implementation
     * @dev Called only once
     * @param _poolImplementationAddress Address of pool implementation
     */
    function initialize(
        address _poolImplementationAddress,
        address _vestingImplementationAddress
    ) external initializer {
        poolImplementationAddress = _poolImplementationAddress;
        vestingImplementationAddress = _vestingImplementationAddress;
        _setupRole(OWNER_ROLE, _msgSender());
        _setRoleAdmin(OWNER_ROLE, OWNER_ROLE);
    }

    /**
     * @notice Set or change address of pool implementation
     * @dev Only admin can can call it
     * @param _poolImplementationAddress Address of new pool implementation
     */
    function setPoolImplementation(
        address _poolImplementationAddress
    ) external onlyOwner {
        _validAddress(_poolImplementationAddress);
        address oldPoolImplementation = poolImplementationAddress;
        poolImplementationAddress = _poolImplementationAddress;
        emit UpdatePoolImplementation(
            oldPoolImplementation,
            _poolImplementationAddress
        );
    }

    /**
     * @notice Set or change address of vesting implementation
     * @dev Only admin can can call it
     * @param _vestingImplementationAddress Address of new vesting implementation
     */
    function setVestingImplementation(
        address _vestingImplementationAddress
    ) external onlyOwner {
        _validAddress(_vestingImplementationAddress);
        address oldVestingImplementation = vestingImplementationAddress;
        vestingImplementationAddress = _vestingImplementationAddress;
        emit UpdateVestingImplementation(
            oldVestingImplementation,
            _vestingImplementationAddress
        );
    }

    /**
     * @notice Create new pool. One pool has 2 sub-pool: galaxy pool and crowfunding pool, with 2 different participation fee.
     * In crowfunding pool, there is a part for WHALE to buy early (called EARLY ACCESS) and another for NORMAL user to buy
     * (called NORMAL ACCESS). Duration of galaxy pool and EARLY ACCESS are same and before duration of NORMAL ACCESS.
     *
     * There are 3 types of profit: participation fee, token fee and collaborator profit.
     * Token fee + Collaborator profit = total raise amount (IDO token) * price = purchased amount (purchase token)
     * Investors need to pay purchase amount and participation fee
     *
     * If project is success (not be cancelled by admin or funded enough IDO token),
     *  - System's admin claims participation fee and token fee
     *    (call pool.claimParticipationFee(), pool.claimTokenFee())
     *  - Collaborator claims collaborator profit (purchased amount - token fee) based on vesting rule
     *    (call pool.claimProfit())
     *  - Investors claim IDO token based on vesting rule
     *  - Collaborator withdraws redundant IDO token
     *    (call pool.withdrawRedundantIDOToken())
     *
     * If project is fail (cancelled or not be funded enough IDO token) (of course before TGE date)
     *  - System's admin claims participation fee
     *    (call pool.claimParticipationFee())
     *  - Investors withdraw purchased amount
     *    (call pool.withdrawPurchasedAmount())
     *  - Collaborator withdraws funded IDO token
     *    (call pool.withdrawRedundantIDOToken())
     *
     * @dev Only has one pool address respectively for one input params
     * @param addrs Array of address includes:
     * - address of IDO token,
     * - address of purchase token
     * @param uints Array of pool information includes:
     * - max purchase amount for KYC user,
     * - max purchase amount for Not KYC user,
     * - token project fee percentage, // will be sent to admin if success or investor in vice versa
     * - galaxy participation fee percentage, // will be sent to admin
     * - crowdfunding participation fee percentage, // will be sent to admin
     * - galaxy pool proportion, (ratio with all project)
     * - early access proportion, (ratio with only crowdfunding pool)
     * - total raise amount,
     * - whale open time,
     * - whale duration,
     * - community duration,
     * - rate of IDO token (based on formula in README),
     * - decimal of IDO token (based on formula in README, is different from decimals in contract of IDO token),
     * - TGE date,
     * - TGE percentage,
     * - vesting cliff,
     * - vesting frequency,
     * - number of vesting release
     * @param dbProjectId Project Id in database
     * @return pool Address of new pool
     */
    function createPool(
        address[2] memory addrs,
        uint[18] memory uints,
        uint dbProjectId
    ) external returns (address pool) {
        bytes32 salt = keccak256(
            abi.encode(addrs, uints, _msgSender(), dbProjectId)
        );

        pool = Clones.cloneDeterministic(poolImplementationAddress, salt);
        IPool(pool).initialize(addrs, uints, _msgSender());

        bytes32 poolInfoHash = keccak256(abi.encode(addrs, uints, dbProjectId));

        emit PoolCreated(poolInfoHash, pool);

        return pool;
    }

    function createVesting() external returns (address) {
        address vesting = Clones.clone(vestingImplementationAddress);
        emit VestingCreated(_msgSender(), vesting);
        return vesting;
    }

    // ============================== INTERNAL FUNCTION ==============================

    /**
     * @dev Check whether or not an address is zero address
     * @param _address An address
     */
    function _validAddress(address _address) internal pure {
        require(_address != address(0), Errors.ZERO_ADDRESS_NOT_VALID);
    }
}

contracts/core/Pool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../interfaces/IPool.sol";
import "./PoolStorage.sol";
import "./BasePausable.sol";
import "../libraries/SafeCast.sol";
import "../libraries/ECDSA.sol";
import "../extensions/IgnitionList.sol";
import "../logics/PoolLogic.sol";
import "../logics/VestingLogic.sol";

contract Pool is IgnitionList, IPool, PoolStorage, BasePausable {
    using SafeERC20 for IERC20;
    using SafeERC20 for IERC20withDec;
    using SafeCast for uint;

    // ============================== EVENT ==============================

    event UpdateRoot(bytes32 root);

    event CancelPool(address indexed pool, bool permanentDeleteStatus);

    event BuyToken(
        address indexed buyer,
        address indexed pool,
        uint purchaseAmount,
        uint8 poolType
    );

    event UpdateTime(
        uint64 whaleOpenTime,
        uint64 whaleCloseTime,
        uint64 communityOpenTime,
        uint64 communityCloseTime
    );

    event FundIDOToken(IERC20withDec IDOToken, uint fundAmount);

    event ClaimTokenFee(address beneficiary, uint tokenFee);

    event ClaimParticipationFee(
        address beneficiary,
        uint participationFeeAmount
    );

    event WithdrawPurchasedAmount(
        address sender,
        address beneficiary,
        uint principalAmount
    );

    event ClaimProfit(address beneficiary, uint claimableAmount);

    // ============================== MODIFIER ==============================

    /**
     * @dev Check whether or not sender of transaction has admin role
     */
    modifier onlyAdmin() {
        require(ignitionFactory.isOwner(_msgSender()), Errors.CALLER_NOT_ADMIN);
        _;
    }

    modifier onlyFunded() {
        require(vesting.isFunded(), Errors.NOT_FUNDED);
        _;
    }

    modifier beforeTGEDate() {
        (uint64 _TGEDate, , , , ) = vesting.getVestingInfo();
        require(
            block.timestamp < _TGEDate,
            Errors.NOT_ALLOWED_TO_DO_AFTER_TGE_DATE
        );
        _;
    }

    modifier afterTGEDate() {
        (uint64 _TGEDate, , , , ) = vesting.getVestingInfo();
        require(
            block.timestamp >= _TGEDate,
            Errors.NOT_ALLOWED_TO_TRANSFER_BEFORE_TGE_DATE
        );
        _;
    }

    // ============================== EXTERNAL FUNCTION ==============================

    /**
     * @notice Initialize a pool with its information
     * @param addrs Array of address includes:
     * - address of IDO token,
     * - address of purchase token
     * @param uints Array of pool information includes:
     * - max purchase amount for KYC user,
     * - max purchase amount for Not KYC user,
     * - token fee percentage,
     * - galaxy participation fee percentage,
     * - crowdfunding participation fee percentage,
     * - galaxy pool proportion,
     * - early access proportion,
     * - total raise amount,
     * - whale open time,
     * - whale duration,
     * - community duration,
     * - rate of IDO token (based on README formula),
     * - decimal of IDO token (based on README formula, is different from decimals in contract of IDO token),
     * - TGE date,
     * - TGE percentage,
     * - vesting cliff,
     * - vesting frequency,
     * - number of vesting release
     */
    function initialize(
        address[2] memory addrs,
        uint[18] memory uints,
        address owner
    ) external initializer {
        __BasePausable__init(owner);
        PoolLogic.verifyPoolInfo(addrs, uints);
        {
            ignitionFactory = IIgnitionFactory(_msgSender());
        }
        _createAndSetVesting(
            addrs[0],
            uints[13],
            uints[14],
            uints[15],
            uints[16],
            uints[17]
        );
        {
            purchaseToken = IERC20(addrs[1]);
        }
        {
            maxPurchaseAmountForKYCUser = uints[0];
            maxPurchaseAmountForNotKYCUser = uints[1];
        }
        {
            tokenFeePercentage = SafeCast.toUint16(uints[2]);
            galaxyParticipationFeePercentage = SafeCast.toUint16(uints[3]);
            crowdfundingParticipationFeePercentage = SafeCast.toUint16(
                uints[4]
            );
        }
        {
            galaxyPoolProportion = SafeCast.toUint16(uints[5]);
            earlyAccessProportion = SafeCast.toUint16(uints[6]);
            totalRaiseAmount = uints[7];

            maxPurchaseAmountForGalaxyPool =
                (uints[7] * uints[5]) /
                PERCENTAGE_DENOMINATOR;
            maxPurchaseAmountForEarlyAccess =
                (uints[7] * (PERCENTAGE_DENOMINATOR - uints[5]) * uints[6]) /
                PERCENTAGE_DENOMINATOR /
                PERCENTAGE_DENOMINATOR;
        }
        {
            whaleOpenTime = SafeCast.toUint64(uints[8]);
            communityOpenTime = whaleCloseTime = SafeCast.toUint64(
                uints[8] + uints[9]
            );
            communityCloseTime = SafeCast.toUint64(
                communityOpenTime + uints[10]
            );
        }
        {
            offeredCurrency.rate = uints[11];
            offeredCurrency.decimal = uints[12];
        }

        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
                ),
                keccak256(bytes(name)),
                keccak256(bytes(version)),
                block.chainid,
                address(this)
            )
        );
    }

    /**
     * @notice Set merkle tree root after snapshoting information of investor
     * @dev Only admin can call it
     * @param _root Root of merkle tree
     */
    function setRoot(bytes32 _root) external onlyAdmin {
        root = _root;
        emit UpdateRoot(root);
    }

    /**
     * @notice Cancel pool: cancel project, nobody can buy token
     * @dev Only admin can call it
     */
    function cancelPool(
        bool _permanentDelete
    ) external onlyAdmin beforeTGEDate {
        _pause();
        vesting.setClaimableStatus(false);
        emit CancelPool(address(this), _permanentDelete);
    }

    /**
     * @notice Update time for galaxy pool and crowdfunding pool
     * @dev Only admin can call it, galaxy pool must be closed before crowdfunding pool
     * @param _newWhaleCloseTime New close time of galaxy pool
     * @param _newCommunityCloseTime New close time of crowdfunding pool
     */
    function updateTime(
        uint64 _newWhaleCloseTime,
        uint64 _newCommunityCloseTime
    ) external onlyAdmin {
        (uint64 _TGEDate, , , , ) = vesting.getVestingInfo();
        require(
            whaleOpenTime < _newWhaleCloseTime &&
                _newWhaleCloseTime < _newCommunityCloseTime &&
                _newCommunityCloseTime <= _TGEDate,
            Errors.INVALID_TIME
        );

        communityOpenTime = whaleCloseTime = _newWhaleCloseTime;
        communityCloseTime = _newCommunityCloseTime;

        emit UpdateTime(
            whaleOpenTime,
            whaleCloseTime,
            communityOpenTime,
            communityCloseTime
        );
    }

    function updateTGEDate(
        uint64 _newTGEDate
    ) external onlyAdmin beforeTGEDate {
        require(communityCloseTime <= _newTGEDate, Errors.INVALID_TIME);
        vesting.updateTGEDate(_newTGEDate);
    }

    /**
     * @notice Investor buy token in galaxy pool
     * @dev Must be in time for whale and pool is not closed
     * @param proof Respective proof for a leaf, which is respective for investor in merkle tree
     * @param _purchaseAmount Purchase amount of investor
     * @param _maxPurchaseBaseOnAllocations Max purchase amount base on allocation of whale
     */
    function buyTokenInGalaxyPool(
        bytes32[] memory proof,
        uint _purchaseAmount,
        uint _maxPurchaseBaseOnAllocations
    ) external whenNotPaused nonReentrant {
        require(_validWhaleSession(), Errors.TIME_OUT_TO_BUY_IDO_TOKEN);

        _verifyAllowance(_msgSender(), _purchaseAmount);
        _preValidatePurchaseInGalaxyPool(
            _purchaseAmount,
            _maxPurchaseBaseOnAllocations
        );
        _internalWhaleBuyToken(
            proof,
            _purchaseAmount,
            _maxPurchaseBaseOnAllocations,
            galaxyParticipationFeePercentage,
            uint8(PoolLogic.PoolType.GALAXY_POOL)
        );
        _updatePurchasingInGalaxyPoolState(_purchaseAmount);
    }

    /**
     * @notice Investor buy token in crowdfunding pool
     * @dev Must be in time for crowdfunding pool and pool is not closed
     * @param proof Respective proof for a leaf, which is respective for investor in merkle tree
     * @param _purchaseAmount Purchase amount of investor
     */
    function buyTokenInCrowdfundingPool(
        bytes32[] memory proof,
        uint _purchaseAmount
    ) external whenNotPaused nonReentrant {
        _verifyAllowance(_msgSender(), _purchaseAmount);
        if (_validWhaleSession()) {
            _preValidatePurchaseInEarlyAccess(_purchaseAmount);
            _internalWhaleBuyToken(
                proof,
                _purchaseAmount,
                0,
                crowdfundingParticipationFeePercentage,
                uint8(PoolLogic.PoolType.EARLY_ACCESS)
            );
            _updatePurchasingInEarlyAccessState(_purchaseAmount);
            return;
        }

        require(_validCommunitySession(), Errors.TIME_OUT_TO_BUY_IDO_TOKEN);

        _preValidatePurchase(_purchaseAmount);
        _internalNormalUserBuyToken(proof, _purchaseAmount);
    }

    function setClaimableStatus(bool _status) external onlyAdmin {
        if (_status == true) {
            require(!isFail(), Errors.NOT_ALLOWED_TO_ALLOW_INVESTOR_TO_CLAIM);
        }
        return vesting.setClaimableStatus(_status);
    }

    function fundIDOToken(
        IERC20withDec _IDOToken,
        bytes calldata signature
    ) external onlyOwner whenNotPaused nonReentrant beforeTGEDate {
        IERC20withDec IDOToken = vesting.getIDOToken();

        uint256 fundAmount = getIDOTokenAmountByOfferedCurrency(totalRaiseAmount);
        if (address(IDOToken) != address(0)) {
            IDOToken.safeTransferFrom(
                _msgSender(),
                address(vesting),
                fundAmount
            );
        } else {
            require(
                _verifyFundAllowanceSignature(_IDOToken, signature),
                Errors.INVALID_SIGNER
            );

            vesting.setIDOToken(_IDOToken);
            _IDOToken.safeTransferFrom(
                _msgSender(),
                address(vesting),
                fundAmount
            );
        }

        vesting.setFundedStatus(true);

        emit FundIDOToken(_IDOToken, fundAmount);
    }

    function withdrawRedundantIDOToken(
        address _beneficiary
    ) external onlyOwner afterTGEDate {
        uint vestingIDOBalance = IERC20(vesting.getIDOToken()).balanceOf(
            address(vesting)
        );
        uint redundantAmount;
        if (isFail()) {
            redundantAmount = vestingIDOBalance;
        } else {
            redundantAmount =
                vestingIDOBalance -
                getIDOTokenAmountByOfferedCurrency(purchasedAmount);
        }
        vesting.withdrawRedundantIDOToken(_beneficiary, redundantAmount);
    }

    /// @notice System's admin receive token fee only when project is success after TGE date (not be cancelled by admin or funded enough IDO token)
    /// @param _beneficiary Address to receive token fee
    function claimTokenFee(
        address _beneficiary
    ) external onlyAdmin whenNotPaused onlyFunded nonReentrant afterTGEDate {
        require(
            tokenFeeClaimedStatus == false,
            Errors.NOT_ALLOWED_TO_CLAIM_TOKEN_FEE
        );
        uint tokenFee = (purchasedAmount * tokenFeePercentage) /
            PERCENTAGE_DENOMINATOR;

        purchaseToken.safeTransfer(_beneficiary, tokenFee);
        tokenFeeClaimedStatus = true;

        emit ClaimTokenFee(_beneficiary, tokenFee);
    }

    function claimParticipationFee(
        address _beneficiary
    ) external onlyAdmin nonReentrant afterTGEDate {
        require(
            participationFeeClaimedStatus == false,
            Errors.NOT_ALLOWED_TO_CLAIM_PARTICIPATION_FEE
        );
        purchaseToken.safeTransfer(_beneficiary, participationFeeAmount);
        participationFeeClaimedStatus = true;

        emit ClaimParticipationFee(_beneficiary, participationFeeAmount);
    }

    /// @notice When project is fail (cancelled by admin or not be funded enough IDO token)
    /// @param _beneficiary Address of receiver
    function withdrawPurchasedAmount(
        address _beneficiary
    ) external nonReentrant {
        PurchaseAmount storage userInfo = userPurchasedAmount[_msgSender()];
        uint principalAmount = userInfo.principal;
        require(principalAmount > 0, Errors.ZERO_AMOUNT_NOT_VALID);
        require(
            isFail() && userInfo.withdrawn == 0,
            Errors.NOT_ALLOWED_TO_WITHDRAW_PURCHASED_AMOUNT
        );

        purchaseToken.safeTransfer(_beneficiary, principalAmount);
        userInfo.withdrawn = principalAmount;

        emit WithdrawPurchasedAmount(
            _msgSender(),
            _beneficiary,
            principalAmount
        );
    }

    function claimProfit(
        address _beneficiary
    ) external nonReentrant onlyOwner afterTGEDate {
        require(
            !isFail() && vesting.isClaimable(),
            Errors.NOT_ALLOWED_TO_CLAIM_PURCHASE_TOKEN
        );
        uint claimableAmount = getClaimableProfitAmount();
        require(claimableAmount > 0, Errors.INVALID_CLAIMABLE_AMOUNT);

        claimableAmount = claimableAmount <=
            purchaseToken.balanceOf(address(this))
            ? claimableAmount
            : purchaseToken.balanceOf(address(this));
        profitClaimedAmount += claimableAmount;

        purchaseToken.safeTransfer(_beneficiary, claimableAmount);

        emit ClaimProfit(_beneficiary, claimableAmount);
    }

    // ============================== PUBLIC FUNCTION ==============================

    function isFail() public view returns (bool) {
        (uint64 _TGEDate, , , , ) = vesting.getVestingInfo();
        return (paused() ||
            (!vesting.isFunded() && block.timestamp >= _TGEDate));
    }

    /**
     * @dev Get IDO token amount base on amount of purchase token
     * @param _amount Amount of purchase token
     * @return Return amount of respective IDO token
     */
    function getIDOTokenAmountByOfferedCurrency(
        uint _amount
    ) public view returns (uint) {
        return
            (_amount * offeredCurrency.rate) / (10 ** offeredCurrency.decimal);
    }

    function getClaimableProfitAmount() public view returns (uint) {
        uint tokenFee = (purchasedAmount * tokenFeePercentage) /
            PERCENTAGE_DENOMINATOR;
        uint totalProfitAmount = purchasedAmount - tokenFee;
        (
            uint64 _TGEDate,
            uint16 _TGEPercentage,
            uint64 _vestingCliff,
            uint64 _vestingFrequency,
            uint _numberOfVestingRelease
        ) = vesting.getVestingInfo();

        return
            VestingLogic.calculateClaimableAmount(
                totalProfitAmount,
                profitClaimedAmount,
                _TGEPercentage,
                _TGEDate,
                _vestingCliff,
                _vestingFrequency,
                _numberOfVestingRelease
            );
    }

    // ============================== INTERNAL FUNCTION ==============================

    function _createAndSetVesting(
        address _IDOToken,
        uint _TGEDate,
        uint _TGEPercentage,
        uint _vestingCliff,
        uint _vestingFrequency,
        uint _numberOfVestingRelease
    ) internal {
        address _vesting = IIgnitionFactory(ignitionFactory).createVesting();
        vesting = IVesting(_vesting);
        vesting.initialize(
            address(this),
            _IDOToken,
            _TGEDate,
            _TGEPercentage,
            _vestingCliff,
            _vestingFrequency,
            _numberOfVestingRelease
        );
    }

    function _verifyFundAllowanceSignature(
        IERC20withDec _IDOToken,
        bytes calldata signature
    ) internal view returns (bool) {
        bytes32 symbolHash = keccak256(abi.encodePacked(_IDOToken.symbol()));
        uint8 decimals = _IDOToken.decimals();
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_SEPARATOR,
                keccak256(
                    abi.encode(
                        FUND_TYPEHASH,
                        address(_IDOToken),
                        address(this),
                        symbolHash,
                        decimals
                    )
                )
            )
        );

        return ignitionFactory.isOwner(ECDSA.recover(digest, signature));
    }

    /**
     * @dev Internal function for whale to buy token
     * @param proof Respective proof for a leaf, which is respective for investor in merkle tree
     * @param _purchaseAmount Purchase amount of investor
     * @param _maxPurchaseBaseOnAllocations Max purchase amount base on allocation of whale
     * @param _participationFeePercentage Fee percentage when buying token
     * @param _poolType 0 for galaxy pool, 1 for early access and 2 for normal user in crowdfunding pool
     */
    function _internalWhaleBuyToken(
        bytes32[] memory proof,
        uint _purchaseAmount,
        uint _maxPurchaseBaseOnAllocations,
        uint _participationFeePercentage,
        uint8 _poolType
    ) internal {
        bool verifyWithKYCed = _verifyUser(
            _msgSender(),
            WHALE,
            maxPurchaseAmountForKYCUser,
            _maxPurchaseBaseOnAllocations,
            proof
        );
        if (verifyWithKYCed) {
            _internalBuyToken(
                _msgSender(),
                _purchaseAmount,
                _participationFeePercentage,
                true,
                _poolType
            );
            return;
        }

        bool verifyWithoutKYC = _verifyUser(
            _msgSender(),
            WHALE,
            maxPurchaseAmountForNotKYCUser,
            _maxPurchaseBaseOnAllocations,
            proof
        );

        require(verifyWithoutKYC, Errors.NOT_IN_WHALE_LIST);

        _internalBuyToken(
            _msgSender(),
            _purchaseAmount,
            _participationFeePercentage,
            false,
            _poolType
        );
    }

    /**
     * @dev Internal function for normal user to buy token
     * @param proof Respective proof for a leaf, which is respective for investor in merkle tree
     * @param _purchaseAmount Purchase amount of investor
     */
    function _internalNormalUserBuyToken(
        bytes32[] memory proof,
        uint _purchaseAmount
    ) internal {
        uint8 poolType = uint8(PoolLogic.PoolType.NORMAL_ACCESS);
        bool verifyWithKYCed = _verifyUser(
            _msgSender(),
            NORMAL_USER,
            maxPurchaseAmountForKYCUser,
            0,
            proof
        );
        if (verifyWithKYCed) {
            _internalBuyToken(
                _msgSender(),
                _purchaseAmount,
                crowdfundingParticipationFeePercentage,
                true,
                poolType
            );
            return;
        }

        bool verifyWithoutKYC = _verifyUser(
            _msgSender(),
            NORMAL_USER,
            maxPurchaseAmountForNotKYCUser,
            0,
            proof
        );

        require(verifyWithoutKYC, Errors.NOT_IN_INVESTOR_LIST);

        _internalBuyToken(
            _msgSender(),
            _purchaseAmount,
            crowdfundingParticipationFeePercentage,
            false,
            poolType
        );
    }

    /**
     * @dev Internal function to buy token
     * @param buyer Address of investor
     * @param _purchaseAmount Purchase amount of investor
     * @param _participationFeePercentage Fee percentage when buying token
     * @param _KYCStatus True if investor KYC and vice versa
     * @param _poolType 0 for galaxy pool, 1 for early access and 2 for normal user in crowdfunding pool
     */
    function _internalBuyToken(
        address buyer,
        uint _purchaseAmount,
        uint _participationFeePercentage,
        bool _KYCStatus,
        uint8 _poolType
    ) internal {
        if (_KYCStatus == true) {
            require(
                userPurchasedAmount[buyer].principal + _purchaseAmount <=
                    maxPurchaseAmountForKYCUser,
                Errors.EXCEED_MAX_PURCHASE_AMOUNT_FOR_KYC_USER
            );
        } else {
            require(
                userPurchasedAmount[buyer].principal + _purchaseAmount <=
                    maxPurchaseAmountForNotKYCUser,
                Errors.EXCEED_MAX_PURCHASE_AMOUNT_FOR_NOT_KYC_USER
            );
        }

        uint participationFee = PoolLogic.calculateParticipantFee(
            _purchaseAmount,
            _participationFeePercentage
        );
        _handleParticipationFee(buyer, participationFee);
        _handlePurchaseTokenFund(buyer, _purchaseAmount);

        uint IDOTokenAmount = getIDOTokenAmountByOfferedCurrency(
            _purchaseAmount
        );
        vesting.createVestingSchedule(buyer, IDOTokenAmount);

        emit BuyToken(buyer, address(this), _purchaseAmount, _poolType);
    }

    function _handlePurchaseTokenFund(
        address _buyer,
        uint _purchaseAmount
    ) internal {
        _forwardPurchaseTokenFunds(_buyer, _purchaseAmount);
        _updatePurchasingState(_buyer, _purchaseAmount);
    }

    function _handleParticipationFee(
        address _buyer,
        uint _participationFee
    ) internal {
        if (_participationFee > 0) {
            _forwardParticipationFee(_buyer, _participationFee);
            _updateParticipationFee(_buyer, _participationFee);
        }
    }

    function _forwardParticipationFee(
        address _buyer,
        uint _participationFee
    ) internal {
        purchaseToken.safeTransferFrom(
            _buyer,
            address(this),
            _participationFee
        );
    }

    function _updateParticipationFee(
        address _buyer,
        uint _participationFee
    ) internal {
        userPurchasedAmount[_buyer].fee += _participationFee;
        participationFeeAmount += _participationFee;
    }

    /**
     * @dev Update purchasing amount in galaxy pool
     * @param _purchaseAmount Purchase amount of investor
     */
    function _updatePurchasingInGalaxyPoolState(uint _purchaseAmount) internal {
        purchasedAmountInGalaxyPool += _purchaseAmount;
    }

    /**
     * @dev Update purchasing amount in early access
     * @param _purchaseAmount Purchase amount of investor
     */
    function _updatePurchasingInEarlyAccessState(
        uint _purchaseAmount
    ) internal {
        purchasedAmountInEarlyAccess += _purchaseAmount;
    }

    /**
     * @dev Update purchasing amount, airdrop amount and TGE amount in all pools
     * @param _purchaseAmount Purchase amount of investor
     */
    function _updatePurchasingState(
        address _buyer,
        uint _purchaseAmount
    ) internal {
        userPurchasedAmount[_buyer].principal += _purchaseAmount;
        purchasedAmount += _purchaseAmount;
    }

    /**
     * @dev Transfer purchase token from investor to pool
     * @param buyer Address of investor
     * @param _purchaseAmount Purchase amount of investor
     */
    function _forwardPurchaseTokenFunds(
        address buyer,
        uint _purchaseAmount
    ) internal {
        purchaseToken.safeTransferFrom(buyer, address(this), _purchaseAmount);
    }

    /**
     * @dev Check whether or not purchase amount exceeds max purchase in early access for whale
     * @param _purchaseAmount Purchase amount of investor
     */
    function _preValidatePurchaseInEarlyAccess(
        uint _purchaseAmount
    ) internal view {
        PoolLogic._validAmount(_purchaseAmount);
        require(
            purchasedAmountInEarlyAccess + _purchaseAmount <=
                maxPurchaseAmountForEarlyAccess,
            Errors.EXCEED_MAX_PURCHASE_AMOUNT_FOR_EARLY_ACCESS
        );
    }

    /**
     * @dev Check whether or not purchase amount exceeds amount in all pools
     * @param _purchaseAmount Purchase amount of investor
     */
    function _preValidatePurchase(uint _purchaseAmount) internal view {
        PoolLogic._validAmount(_purchaseAmount);
        require(
            purchasedAmount + _purchaseAmount <= totalRaiseAmount,
            Errors.EXCEED_TOTAL_RAISE_AMOUNT
        );
    }

    /**
     * @dev Check whether or not session of whale
     * @return Return true if yes, and vice versa
     */
    function _validWhaleSession() internal view returns (bool) {
        return
            block.timestamp > whaleOpenTime &&
            block.timestamp <= whaleCloseTime;
    }

    /**
     * @dev Check whether or not session of community user
     * @return Return true if yes, and vice versa
     */
    function _validCommunitySession() internal view returns (bool) {
        return
            block.timestamp > communityOpenTime &&
            block.timestamp <= communityCloseTime;
    }

    /**
     * @dev Verify allowance of investor's token for pool
     * @param _user Address of investor
     * @param _purchaseAmount Purchase amount of investor
     */
    function _verifyAllowance(
        address _user,
        uint _purchaseAmount
    ) private view {
        uint allowance = purchaseToken.allowance(_user, address(this));
        require(allowance >= _purchaseAmount, Errors.NOT_ENOUGH_ALLOWANCE);
    }

    /**
     * @dev Check whether or not purchase amount exceeds max purchase amount base on allocation for whale
     * @param _purchaseAmount Amount of purchase token
     * @param _maxPurchaseBaseOnAllocations Max purchase amount base on allocations for whale
     */
    function _preValidatePurchaseInGalaxyPool(
        uint _purchaseAmount,
        uint _maxPurchaseBaseOnAllocations
    ) internal pure {
        PoolLogic._validAmount(_purchaseAmount);
        require(
            _purchaseAmount <= _maxPurchaseBaseOnAllocations,
            Errors.EXCEED_MAX_PURCHASE_AMOUNT_FOR_USER
        );
    }
}

contracts/core/PoolStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../interfaces/IIgnitionFactory.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../interfaces/IVesting.sol";

contract PoolStorage {
    /// @dev rate and decimal to display price of IDO token
    struct OfferedCurrency {
        uint rate;
        uint decimal;
    }

    /// @dev amount of purchase token, fee used to buy IDO token and withdrawn amount status if project failed
    struct PurchaseAmount {
        uint principal; // purchased amount(based on purchased token)
        uint fee; // participation fee (based on purchased token)
        uint withdrawn;
    }

    /// @dev keccak256("WHALE")
    bytes32 public constant WHALE =
        0xed4b80c86c7954bdbf516c492acb4a2899eb0ee85b7c74e26d85e55a07562c95;

    /// @dev keccak256("NORMAL_USER")
    bytes32 public constant NORMAL_USER =
        0x13e31188d81b941f4c541528790db4031bef078b78d364bde6fc2d4e5ad79e01;

    /// @dev Percentage denominator
    uint16 public constant PERCENTAGE_DENOMINATOR = 10000;

    // --- EIP712 niceties ---
    bytes32 public DOMAIN_SEPARATOR;

    // bytes32 public constant FUND_TYPEHASH = keccak256("Fund(address IDOToken,address pool,bytes32 symbolHash,uint8 decimals)");
    bytes32 public constant FUND_TYPEHASH =
        0x041776e07c284720eefe91849b7eb530d952f126b19af710c29ea3cc06693b97;

    /// @dev Name used for fund signature
    string public constant name = "Pool";

    /// @dev Version used for fund signature
    string public constant version = "1";

    /// @dev Address of pool factory
    IIgnitionFactory public ignitionFactory;

    /// @dev Address of purchase token
    IERC20 public purchaseToken;

    /// @dev Store rate and decimal to display price of IDO token
    OfferedCurrency public offeredCurrency;

    /// @dev Max purchase amount for galaxy pool = total raise amount * galaxy pool proportion
    uint public maxPurchaseAmountForGalaxyPool;

    /// @dev Max purchase amount for early access = (total raise amount - total raise amount * galaxy pool proportion) * early access proportion
    uint public maxPurchaseAmountForEarlyAccess;

    /// @dev Max purchase amount for KYC user
    uint public maxPurchaseAmountForKYCUser;

    /// @dev Max purchase amount for NOT KYC user
    uint public maxPurchaseAmountForNotKYCUser;

    /// @dev Token fee to create project
    uint16 public tokenFeePercentage;

    /// @dev True if token fee is claimed
    bool public tokenFeeClaimedStatus;

    /// @dev Fee percentage when buying token in galaxy pool
    uint16 public galaxyParticipationFeePercentage;

    /// @dev Fee percentage when buying token in crowdfunding pool
    uint16 public crowdfundingParticipationFeePercentage;

    /// @dev Proportion of total raise for galaxy pool
    uint16 public galaxyPoolProportion;

    /// @dev Proportion of crowdfunding pool amount for early access
    uint16 public earlyAccessProportion;

    /// @dev Total raise amount of all pools (based on purchase token)
    uint public totalRaiseAmount;

    /// @dev Open time of galaxy pool
    uint64 public whaleOpenTime;

    /// @dev Close time of galaxy pool
    uint64 public whaleCloseTime;

    /// @dev Open time for community user = Close time of galaxy pool
    uint64 public communityOpenTime;

    /// @dev Close time of crowdfunding pool
    uint64 public communityCloseTime;

    /// @dev Participation fee in all sub-pool (based on purchase token)
    uint public participationFeeAmount;

    /// @dev True if participation fee is claimed
    bool public participationFeeClaimedStatus;

    /// @dev Purchased amount in galaxy pool (based on purchase token), do not include participation fee
    uint public purchasedAmountInGalaxyPool;

    /// @dev Purchased amount in early access (based on purchase token), do not include participation fee
    uint public purchasedAmountInEarlyAccess;

    /// @dev Purchased amount in all pools (based on purchase token), do not include participation fee
    uint public purchasedAmount;

    /// @dev Profit amount which is claimed by collaborator (exclude token fee)
    uint public profitClaimedAmount;

    /// @dev Mapping from User to purchased amount (based on purchase token)
    mapping(address => PurchaseAmount) public userPurchasedAmount;

    /// @dev Vesting contract address
    IVesting public vesting;
}

contracts/core/Vesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../interfaces/IVesting.sol";
import "./VestingStorage.sol";
import "./BasePausable.sol";
import "../libraries/SafeCast.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../logics/VestingLogic.sol";

contract Vesting is IVesting, VestingStorage, BasePausable {
    using SafeERC20 for IERC20withDec;

    // ============================== EVENT ==============================

    event SetClaimableStatus(bool status);

    event UpdateTGEDate(uint64 newTGEDate);

    event SetIDOTokenAddress(address IDOToken);

    event Funded(bool status);

    event WithdrawRedundantIDOToken(
        address beneficiary,
        uint redundantAmount
    );

    event ClaimIDOToken(
        address sender,
        address beneficiary,
        uint claimableAmount
    );

    // ============================== MODIFIER ==============================
    modifier afterTGEDate() {
        require(
            block.timestamp >= TGEDate,
            Errors.NOT_ALLOWED_TO_TRANSFER_BEFORE_TGE_DATE
        );
        _;
    }

    modifier onlySatisfyClaimCondition() {
        require(
            isFunded() && isClaimable(),
            Errors.NOT_ALLOWED_TO_CLAIM_IDO_TOKEN
        );
        _;
    }

    // ============================== EXTERNAL FUNCTION ==============================

    function initialize(
        address owner,
        address _IDOToken,
        uint _TGEDate,
        uint _TGEPercentage,
        uint _vestingCliff,
        uint _vestingFrequency,
        uint _numberOfVestingRelease
    ) external override initializer {
        VestingLogic._verifyVestingInfo(_TGEPercentage);
        IDOToken = IERC20withDec(_IDOToken);
        TGEDate = SafeCast.toUint64(_TGEDate);
        TGEPercentage = SafeCast.toUint16(_TGEPercentage);
        vestingCliff = SafeCast.toUint64(_vestingCliff);
        vestingFrequency = SafeCast.toUint64(_vestingFrequency);
        numberOfVestingRelease = _numberOfVestingRelease;
        claimable = true;

        _setupRole(OWNER_ROLE, owner);
        _setRoleAdmin(OWNER_ROLE, OWNER_ROLE);
    }

    function createVestingSchedule(
        address _user,
        uint _totalAmount
    ) external onlyOwner {
        require(_totalAmount > 0, Errors.ZERO_AMOUNT_NOT_VALID);

        vestingAmountInfo[_user].totalAmount += _totalAmount;
    }

    function setIDOToken(IERC20withDec _IDOToken) external onlyOwner {
        IDOToken = _IDOToken;
        emit SetIDOTokenAddress(address(_IDOToken));
    }

    function setFundedStatus(bool _status) external onlyOwner {
        funded = _status;
        emit Funded(_status);
    }

    function setClaimableStatus(bool _status) external onlyOwner {
        claimable = _status;
        emit SetClaimableStatus(_status);
    }

    function updateTGEDate(uint64 _TGEDate) external onlyOwner {
        TGEDate = _TGEDate;
        emit UpdateTGEDate(_TGEDate);
    }

    function claimIDOToken(
        address _beneficiary
    ) external onlySatisfyClaimCondition nonReentrant afterTGEDate {
        VestingAmountInfo storage userInfo = vestingAmountInfo[_msgSender()];
        require(
            userInfo.claimedAmount < userInfo.totalAmount,
            Errors.ALREADY_CLAIM_TOTAL_AMOUNT
        );
        uint claimableAmount = getClaimableAmount(_msgSender());
        require(claimableAmount > 0, Errors.INVALID_CLAIMABLE_AMOUNT);

        claimableAmount = claimableAmount <= IDOToken.balanceOf(address(this))
            ? claimableAmount
            : IDOToken.balanceOf(address(this));
        userInfo.claimedAmount += claimableAmount;

        IERC20withDec(IDOToken).safeTransfer(_beneficiary, claimableAmount);

        emit ClaimIDOToken(_msgSender(), _beneficiary, claimableAmount);
    }

    function withdrawRedundantIDOToken(
        address _beneficiary,
        uint _redundantAmount
    ) external onlyOwner nonReentrant {
        require(_redundantAmount > 0, Errors.ZERO_AMOUNT_NOT_VALID);
        IDOToken.safeTransfer(_beneficiary, _redundantAmount);

        emit WithdrawRedundantIDOToken(_beneficiary, _redundantAmount);
    }

    function getIDOToken() external view returns (IERC20withDec) {
        return IDOToken;
    }

    function getVestingInfo()
        external
        view
        returns (uint64, uint16, uint64, uint64, uint)
    {
        return (
            TGEDate,
            TGEPercentage,
            vestingCliff,
            vestingFrequency,
            numberOfVestingRelease
        );
    }

    // ============================== PUBLIC FUNCTION ==============================

    function isClaimable() public view returns (bool) {
        return claimable;
    }

    function isFunded() public view returns (bool) {
        return funded;
    }

    function getClaimableAmount(address user) public view returns (uint) {
        VestingAmountInfo memory userInfo = vestingAmountInfo[user];
        return
            VestingLogic.calculateClaimableAmount(
                userInfo.totalAmount,
                userInfo.claimedAmount,
                TGEPercentage,
                TGEDate,
                vestingCliff,
                vestingFrequency,
                numberOfVestingRelease
            );
    }
}

contracts/core/VestingStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../interfaces/IERC20withDec.sol";

contract VestingStorage {
    struct VestingAmountInfo {
        uint totalAmount; // total amount will be claimed by investor
        uint claimedAmount; // claimed amount
    }

    /// @notice Percentage denominator
    uint16 public constant PERCENTAGE_DENOMINATOR = 10000;

    /// @notice Address of IDO token
    IERC20withDec public IDOToken;

    /// @notice Time for user to redeem IDO token
    uint64 public TGEDate;

    /// @notice Percentage of IDO token amount of user, which can be redeemed after TGEDate
    uint16 public TGEPercentage;

    /// @notice Vesting cliff
    uint64 public vestingCliff;

    /// @notice Vesting frequency
    uint64 public vestingFrequency;

    /// @notice Number of vesting release
    uint public numberOfVestingRelease;

    /// @notice vesting info of each user
    mapping(address => VestingAmountInfo) public vestingAmountInfo;

    /// @notice True if collaborator fund enough IDO token
    bool public funded;

    /// @notice True if admin allow user to claim
    bool public claimable = true;
}

contracts/extensions/IgnitionList.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.10;

import "../libraries/MerkleProof.sol";

contract IgnitionList {
    bytes32 public root;

    function _verifyUser(
        address _candidate,
        bytes32 _userType,
        uint _maxPurchaseWhetherOrNotKYCAmount,
        uint _maxPurchaseBaseOnAllocations,
        bytes32[] memory proof
    ) internal view returns (bool) {
        // leaf = {address + hash("User type") + max purchase KYC/notKYC amount + max purchase for each user per allocation}
        bytes32 leaf = keccak256(
            abi.encodePacked(
                _candidate,
                _userType,
                _maxPurchaseWhetherOrNotKYCAmount,
                _maxPurchaseBaseOnAllocations
            )
        );
        return MerkleProof.verify(proof, root, leaf);
    }
}

